        ogPosRight = eyeRight.getBoundingClientRect();
    });

    function move(e) {
        const s = 0.01;

        eyeLeft.style.translate = `${(e.clientX - ogPosLeft.x - ogPosLeft.width / 2) * s}px ${(e.clientY - ogPosLeft.y - ogPosLeft.height / 2) * s}px`;
        eyeRight.style.translate = `${(e.clientX - ogPosRight.x - ogPosRight.width / 2) * s}px ${(e.clientY - ogPosRight.y - ogPosRight.height / 2) * s}px`;
        // eyeRight.style.translate = `${rX * s}px ${rY * s}px`;
    }
